import { useState, useContext } from 'react';
import styles from '../../styles/searchbar.module.css';
import Options from './searchbar-options';
import { AppContext } from '../../App.js';

const Searchbar = ({ requestAuthLink, createPlaylist }) => {

  const { creatingPlaylist } = useContext(AppContext);

  const [url, setUrl] = useState('');
  const [options, setOptions] = useState(false);

  return (
    <div>
      <button type="button" className={styles.auth} onClick={requestAuthLink}>Connect Your Spotify Account</button>
      <form className={styles.searchbar}>
        <br />
        <input
          type="text"
          className={styles.url}
          name="searchbar"
          spellCheck="false"
          autoComplete="off"
          placeholder="Setlist Link"
          pattern=".*setlist\.fm.*"
          title="Must be a setlist.fm link"
          required
          value={url}
          onChange={(e) => setUrl(e.target.value)}
        />
        <span onClick={() => setOptions(!options)}>Show options</span>
        { options && <Options /> }
        <button type="button" onClick={() => createPlaylist(url)}>
          { creatingPlaylist ? 'Loading...' : 'Create Playlist' }
        </button>
      </form>
    </div>
  )
}

export default Searchbar
