import { useContext } from 'react';
import { AppContext } from '../App.js';
import styles from '../styles/playlist.module.css';
import { Link } from 'react-router-dom';
import Covers from '../components/playlist/covers.js';
import Unfound from '../components/playlist/unfound.js';

const Playlist = () => {

  const { playlistLink, unfoundSongs, coverSongs } = useContext(AppContext);

  const [copiedLink, setCopiedLink] = useState(false);

  function copyLink(link) {
    if ('clipboard' in navigator) {
      navigator.clipboard
      .writeText(link)
    }
  }

  function copySong(song) {
    const text = song.artist ? `${song.name} by ${song.artist}` : `${song.name}`;
    if ('clipboard' in navigator) {
      navigator.clipboard
      .writeText(text)
    }
  }

  return (
    <div className={styles.playlist}>
      { playlistLink &&
        <div className={styles.created}>
          <h2>Your playlist has been created</h2>
          <span>It's already your saved in Spotify account and you can also find it at the following link:</span>
          <div className={styles.linkContainer}>
            <a href={playlistLink} className={styles.link} target='_blank' rel='noopener noreferrer'>{ playlistLink }</a>
            <div className={styles.buttons}>
              <a href={playlistLink} target='_blank' rel='noopener noreferrer'>Play</a>
              <span onClick={() => copyLink(playlistLink)}>{ copiedLink ? 'Copied' : 'Copy' }</span>
            </div>
          </div>
          { unfoundSongs.length > 0 &&
            <Unfound songs={unfoundSongs} copySong={copySong} />
          }
          { coverSongs.length > 0 &&
            <Covers songs={coverSongs} copySong={copySong} />
          }
        </div>
      }
      { !playlistLink &&
        <div className={styles.failed}>
          <span>Cannot find playlist, please return <Link to="/">home</Link> and try again</span>
        </div>
      }
    </div>
  )
}

export default Playlist;
