import { useEffect } from 'react';
import { Link } from 'react-router-dom';
import styles from '../styles/home.module.css';
import Searchbar from '../components/searchbar/searchbar';

const Home = () => {

  let redirect_uri = 'http://localhost:3000/';

  let access_token;
  let refresh_token;
  const client_id = '0d453f304c284edca80fef86ba63f57c';
  const client_secret = 'd4a0686ea42f418a92521bd7e884d221';

  const TOKEN = "https://accounts.spotify.com/api/token";
  const AUTHORIZE = "https://accounts.spotify.com/authorize";

  useEffect(() => {
    onPageLoad()
  }, [])

  function requestAuthorization(){
    let url = AUTHORIZE;
    url += "?client_id=" + client_id;
    url += "&response_type=code";
    url += "&redirect_uri=" + encodeURI(redirect_uri);
    url += "&show_dialog=true";
    url += "&scope=user-read-private user-read-private playlist-modify-private playlist-modify-public";
    window.location.href = url; // Show Spotify's authorization screen
  }

  function onPageLoad() {
    console.log('Page Loaded');
    if (window.location.search.length > 0) {
      console.log('Redirecting');
      handleRedirect();
    }
  }

  function handleRedirect() {
    let code = getCode();
    fetchAccessToken(code);
  }

  function getCode() {
    console.log('Getting code');
    let code = null;
    const queryString = window.location.search;
    if (queryString.length > 0) {
      const urlParams = new URLSearchParams(queryString);
      code = urlParams.get('code');
    }
    return code;
  }

  function fetchAccessToken(code) {
    console.log('Making access token');
    let body = "grant_type=authorization_code";
    body += "&code=" + code;
    body += "&redirect_uri=" + encodeURI(redirect_uri);
    body += "&client_id=" + client_id;
    body += "&client_secret=" + client_secret;
    callAuthorizationApi(body);
  }

  function callAuthorizationApi(body) {
    console.log('Calling Auth API');
    let xhr = new XMLHttpRequest();
    xhr.open("POST", TOKEN, true);
    xhr.setRequestHeader('Content-Type', 'application/x-www-form-urlencoded');
    xhr.setRequestHeader('Authorization', 'Basic ' + btoa(client_id + ":" + client_secret));
    xhr.send(body);
    xhr.onload = handleAuthorizationResponse;
  }

  function handleAuthorizationResponse() {
    console.log('Handling Auth Response');
    if (this.status === 200) {
      console.log('Sucessful Response');
      var data = JSON.parse(this.responseText);
      access_token = data.access_token;
      refresh_token = data.refresh_token;
      console.log(access_token);
      console.log(refresh_token);
      // onPageLoad();
    }
    else {
      console.log('Unsucessful Response');
      console.log(this.responseText);
    }
  }


  return (
    <div className={styles.home}>
      <div className={styles.header}>
        <h2>Instantly create a Spotify playlist from any setlist!</h2>
        <span> All you have to do is paste the link from setlist.fm, we look after the rest.</span>
      </div>
      <div className={styles.searchbar}>
        <Searchbar requestAuthorization={requestAuthorization}/>
      </div>
      <Link to="/">Home</Link>
      <Link to="/about">About</Link>
    </div>
  )
}

export default Home;
