import { useContext } from 'react';
import { AppContext } from '../App.js';
import { Link, useNavigate } from 'react-router-dom';
import styles from '../styles/playlist.module.css';
import Unfound from '../components/playlist/unfound.js';

const Playlist = () => {

  const { playlistID, setPlaylistID, playlistLink, setPlaylistLink, unfoundSongs, setUnfoundSongs } = useContext(AppContext);

  return (
    <div className={styles.playlist}>
      { playlistLink &&
        <div className={styles.created}>
          <h2>Your playlist has been created</h2>
          <br/>
          <span>It's in the 'Playlist' section of your Spotify or you can access it at the following link:</span>
          <br/>
          <a>{ playlistLink }</a>
          <Link to={{ pathname: "https://herewecode.io/" }} target="_blank">Click to open HereWeCode (new tab)</Link>
          <br/>
          { unfoundSongs.length > 0 &&
            <Unfound songs={unfoundSongs}/>
          }
        </div>
      }
      { !playlistLink &&
        <div className={styles.failed}>
        </div>
      }
    </div>
  )
}

export default Playlist;
