import './styles/App.css';
import React, { useState } from 'react';
import { Route, Routes, Link } from 'react-router-dom';
import Layout from './components/layout/layout';
import Home from './views/home';
import Playlist from './views/playlist';

export const AppContext = React.createContext();

function App() {

  const [creatingPlaylist, setCreatingPlaylist] = useState(false);

  const [playlistID, setPlaylistID] = useState();
  const [playlistLink, setPlaylistLink] = useState();
  const [unfoundSongs, setUnfoundSongs] = useState();
  const [coverSongs, setCoverSongs] = useState();
  const [playlistName, setPlaylistName] = useState('');
  const [playlistDescription, setPlaylistDescription] = useState('');

  return (
    <AppContext.Provider value={ { playlistID, setPlaylistID, playlistLink, setPlaylistLink, unfoundSongs, setUnfoundSongs, coverSongs, setCoverSongs, creatingPlaylist, setCreatingPlaylist, playlistName, setPlaylistName, playlistDescription, setPlaylistDescription } }>
      <Layout>
      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/playlist" element={<Playlist />} />
      </Routes>
      </Layout>
    </AppContext.Provider>
  );
}

export default App;
