import { useContext } from 'react';
import { AppContext } from '../App.js';
import Unfound from '../components/playlist/unfound.js'

const Playlist = () => {

  const { playlistID, setPlaylistID, playlistLink, setPlaylistLink, unfoundSongs, setUnfoundSongs } = useContext(AppContext);

  return (
    <div>
      { playlistLink &&
        <>
          <span>Your playlist has been created</span>
          <span>It's in the 'Playlist' section of your Spotify or you can access it at the following link:</span>
          <span>{ playlistLink }</span>
          { console.log(unfoundSongs) }
          { unfoundSongs.length > 0 ? (
            <>
              <span>We were unable to find { unfoundSongs.length } from the setlist on the Spotify. The song names are:</span>
              <div>
                { unfoundSongs.map((song, i) => (
                  <span key={i}>{ song }</span>
                ))}
              </div>
            </>
          ) : (
            <span>Nothing missing</span>
          )
          }
        </>
      }
    </div>
  )
}

export default Playlist;
