import { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import styles from '../styles/home.module.css';
import Searchbar from '../components/searchbar/searchbar';
import { AppContext } from '../App.js'

const Home = () => {

  const { playlistID, setPlaylistID, playlistLink, setPlaylistLink } = useContext(AppContext);

  let access_token;
  let refresh_token;

  const TOKEN = "https://accounts.spotify.com/api/token";

  useEffect(() => {
    onPageLoad()
  }, [])

  async function requestAuthLink(){
    const data = await fetch('http://localhost:4000/auth');
    const json = await data.json();
    console.log(json.auth);
    const url = json.auth;
    window.location.href = url; // Show Spotify's authorization screen
  }

  function onPageLoad() {
    console.log('Page Loaded');
    if (window.location.search.length > 0) {
      handleRedirect();
    }
  }

  function handleRedirect() {
    let code = getCode();
    fetchAccessToken(code);
  }

  function getCode() {
    let code;
    const queryString = window.location.search;
    if (queryString.length > 0) {
      const urlParams = new URLSearchParams(queryString);
      code = urlParams.get('code');
    }
    return code;
  }

  async function fetchAccessToken(code) {
    const data = await fetch('http://localhost:4000/access');
    const json = await data.json();
    const query1 = json.query1;
    const query2 = json.query2;
    const query = query1 + '&code=' + code + query2;
    callAuthorizationApi(query);
  }

  function callAuthorizationApi(body) {
    console.log('Fetching Access Token');
    let xhr = new XMLHttpRequest();
    xhr.open("POST", TOKEN, true);
    xhr.setRequestHeader('Content-Type', 'application/x-www-form-urlencoded');
    xhr.setRequestHeader('Authorization', 'Basic ' + btoa('0d453f304c284edca80fef86ba63f57c' + ":" + 'd4a0686ea42f418a92521bd7e884d221'));
    xhr.send(body);
    xhr.onload = handleAuthorizationResponse;
  }

  function handleAuthorizationResponse() {
    if (this.status === 200) {
      var data = JSON.parse(this.responseText);
      access_token = data.access_token;
      refresh_token = data.refresh_token;
      console.log(access_token);
      console.log(refresh_token);
      // onPageLoad();
    }
    else {
      console.log('Unsucessful Response');
      console.log(this.responseText);
    }
  }

  async function createPlaylist() {
    console.log('Creating Playlist');
    const data = await fetch('http://localhost:4000/setlist/4bbf3316');
    const json = await data.json();
    setPlaylistID(json.playlistID);
    setPlaylistLink(json.playlistLink);
    console.log(playlistID);
    console.log(playlistLink);
    window.location.href = 'http://localhost:3000/playlist';
  }


  return (
    <div className={styles.home}>
      <div className={styles.header}>
        <h2>Instantly create a Spotify playlist from any setlist!</h2>
        <span> All you have to do is paste the link from setlist.fm, we look after the rest.</span>
      </div>
      <div className={styles.searchbar}>
        <Searchbar requestAuthLink={requestAuthLink} createPlaylist={createPlaylist}/>
      </div>
      <Link to="/">Home</Link>
      <Link to="/about">About</Link>
    </div>
  )
}

export default Home;

// Handle user not approving access in Spotify auth flow: https://developer.spotify.com/documentation/general/guides/authorization/code-flow/ (Under response)
// Change show dialog to false in production to prevent need for keep signing in
