import { useState, useContext } from 'react';
import styles from '../../styles/searchbar.module.css';
import Options from './searchbar-options';
import { AppContext } from '../../App.js';

const Searchbar = ({ requestAuthLink, createPlaylist }) => {

  const { creatingPlaylist } = useContext(AppContext);

  const [url, setUrl] = useState('');
  const [options, setOptions] = useState(false);

  return (
    <div className={styles.searchbar}>
      // <button type="button" className={styles.auth} onClick={requestAuthLink}>First, Connect Your Spotify Account</button>
      <form className={styles.form}>
        <input
          type="text"
          className={styles.url}
          name="searchbar"
          spellCheck="false"
          autoComplete="off"
          placeholder="Setlist Link"
          pattern=".*setlist\.fm.*"
          title="Must be a setlist.fm link"
          required
          value={url}
          onChange={(e) => setUrl(e.target.value)}
        />
        <div className={styles.optionsButton} onClick={() => setOptions(!options)}>
          <span>Options</span>
          <svg width="1em" height="1em" viewBox="0 0 24 24">
            <path fill="currentColor" d="m12 15.4l-6-6L7.4 8l4.6 4.6L16.6 8L18 9.4Z"/>
          </svg>
        </div>
        { options && <Options /> }
        <button type="button" className={styles.searchButton} onClick={() => createPlaylist(url)}>
          { creatingPlaylist ? 'Loading...' : 'Create Playlist' }
        </button>
      </form>
    </div>
  )
}

export default Searchbar
