import { useState, useContext } from 'react';
import styles from '../../styles/searchbar.module.css';
import Options from './searchbar-options';
import { AppContext } from '../../App.js';

const Searchbar = ({ requestAuthLink, createPlaylist }) => {

  const { creatingPlaylist } = useContext(AppContext);

  const [url, setUrl] = useState('');
  const [options, setOptions] = useState(false);
  const [arrow, setArrow] = useState(false);

  function toggleOptions() {
    setArrow(!arrow);
    setOptions(!options);
  }

  return (
    <div className={styles.searchbar}>
      <button type="button" className={styles.auth} onClick={requestAuthLink}>First, Connect Your Spotify Account</button>
      <form className={styles.form}>
        <input
          type="text"
          className={styles.url}
          name="searchbar"
          spellCheck="false"
          autoComplete="off"
          placeholder="Setlist Link"
          pattern=".*setlist\.fm.*"
          title="Must be a setlist.fm link"
          required
          value={url}
          onChange={(e) => setUrl(e.target.value)}
        />
        <div className={styles.optionsButton} onClick={toggleOptions}>
          <span>Options</span>
          <svg className={arrow ? styles.arrow : ''} viewBox="0 0 24 24">
            <path fill="currentColor" d="m12 15.4l-6-6L7.4 8l4.6 4.6L16.6 8L18 9.4Z"/>
          </svg>
        </div>
        { options && <Options /> }
        <button type="button" className={styles.searchButton} onClick={() => createPlaylist(url)}>
          { creatingPlaylist ?
            <div>
              <span>Creating</span>
              <svg width="1em" height="1em" preserveAspectRatio="xMidYMid meet" viewBox="0 0 24 24">
                <path fill="none" stroke="currentColor" stroke-dasharray="15" stroke-dashoffset="15" stroke-linecap="round" stroke-width="2" d="M12 3C16.9706 3 21 7.02944 21 12">
                <animate fill="freeze" attributeName="stroke-dashoffset" dur="0.3s" values="15;0"/>
                <animateTransform attributeName="transform" dur="1.5s" repeatCount="indefinite" type="rotate" values="0 12 12;360 12 12"/>
                </path>
              </svg>
            </div>
          :
            'Create Playlist' }
        </button>
      </form>
    </div>
  )
}

export default Searchbar
