import { useState } from 'react';
import styles from '../../styles/searchbar.module.css';
import Options from './searchbar-options';

const Searchbar = ({ requestAuthLink, createPlaylist }) => {

  const [url, setUrl] = useState('');
  const [options, setOptions] = useState(false);

  return (
    <form className={styles.searchbar}>
      <input
        type="text"
        className={styles.url}
        name="searchbar"
        spellCheck="false"
        autoComplete="off"
        placeholder="Setlist Link"
        value={url}
        onChange={(e) => setUrl(e.target.value)}
      />
      <span onClick={() => setOptions(!options)}>Show options</span>
      { options && <Options /> }
      <button type="button" onClick={requestAuthLink}>Connect Account</button>
      <button type="button" onClick={() => createPlaylist(url)}>Create Playlist</button>
    </form>
  )
}

export default Searchbar
