import { useContext } from 'react';
import { AppContext } from '../../App.js';
import styles from '../../styles/searchbar.module.css';

const Options = () => {

  const { playlistID, setPlaylistID, playlistLink, setPlaylistLink, unfoundSongs, setUnfoundSongs, coverSongs, setCoverSongs, creatingPlaylist, setCreatingPlaylist, playlistName, setPlaylistName, playlistDescription, setPlaylistDescription } = useContext(AppContext);

  return (
      <div className={styles.options}>
        <div className={styles.name}>
          <span>Playlist Name:</span>
          <input
            type="text"
            name="name-searchbar"
            spellCheck="false"
            autoComplete="off"
            placeholder="Playlist Name"
            value={playlistName}
          />
        </div>
        <div className={styles.name}>
          <span>Playlist Description:</span>
          <input
          type="text"
          name="description-searchbar"
          spellCheck="false"
          autoComplete="off"
          placeholder="Playlist Description"
          value={playlistDescription}
          />
        </div>
      </div>
  )
}

export default Options
